{"changed":true,"filter":false,"title":"productPreviewModal.vue","tooltip":"/src/components/common/productPreviewModal.vue","value":"<template>\n  \n    <q-modal\n        v-model=\"openModal\"\n        :content-css=\"{minWidth: '80vw', minHeight: '80vh'}\"\n        @open=\"showProductPreview = true\"\n        @close=\"$root.$emit('closedProductPreview'), showProductPreview = false\"\n    >\n        <q-modal-layout id=\"previewContainer\">\n        \n            <q-toolbar slot=\"header\" color=\"grey\" :style=\"toolbarStyle\">\n                <q-btn id=\"closePreviewModal\" flat icon=\"fa-chevron-left\" @click=\"openModal = false\" />\n                <q-toolbar-title>\n                    <h6>Preview</h6>\n                </q-toolbar-title>\n            </q-toolbar>\n            \n            <div :is=\"productPreview\" v-if=\"showProductPreview\" />\n            <!--<defaultProductPreview />-->\n        \n        </q-modal-layout>\n    </q-modal>\n    \n</template>\n\n<script>\n    // TODO switch back to real product preview before push\n    import defaultProductPreview from './shop/productPreview'\n    // var defaultProductPreview = require('../customComponents/gototaltraining/totaltrainingPWYWProductPreview')\n\n    export default {\n        props: ['componentData'],\n        \n        components: {\n            defaultProductPreview\n        },\n\n        data() {\n            return {\n                openModal: false,\n                productPreview: 'defaultProductPreview',\n                showProductPreview: false\n            }\n        },\n\n        computed: {\n            homePageSections() {\n                return this.$root.$store.state.environment.json.homePageSections\n            },\n        \n            brandColor() {\n                return this.$root.$store.state.environment.json ? this.$root.$store.state.environment.json.e.brandColor : ''\n            },\n        \n            toolbarStyle() {\n                return this.brandColor ? 'background: ' + this.brandColor + ' !important ;' : ''\n            },\n        \n            comingFrom() {\n                return this.$root.$store.state.comingFrom\n            }\n        },\n        \n        methods: {\n            checkAndLoadCustomProductPreview() {\n                // k('homePageSections: ', this.homePageSections)\n                var productPreviewComponent = this.homePageSectionFromID('productPreview')\n                k('productPreviewComponent watch: ', productPreviewComponent)\n                \n                if (productPreviewComponent.componentPath) {\n                    this.loadIt(productPreviewComponent.component, 'customComponents/' + productPreviewComponent.componentPath.replace('.html', ''))\n                    this.productPreview = productPreviewComponent.component\n                }\n            }\n        },\n\n        watch: {\n            homePageSections() {\n                this.checkAndLoadCustomProductPreview()\n            }\n        },\n\n        created() {\n            this.$root.$on('openProductPreview', (val) => {\n                // k('event hub openProductPreview: ', val)\n                this.$store.commit('productPreviewModalOpen', true)\n                // this.$refs.productPreview.open()\n                this.openModal = true\n                document.querySelector('#previewContainer > .scroll').scrollTop = 0\n            })\n            \n            this.$root.$on('closeProductPreview', (val) => {\n                // k('event hub closeProductPreview: ', val)\n                this.$store.commit('productPreviewModalOpen', false)\n                this.openModal = false\n                // this.$refs.productPreview.close()\n            })\n            \n            if (this.homePageSections) {\n                this.checkAndLoadCustomProductPreview()\n            }\n            \n        },\n\n        beforeDestroy() {\n\n        }\n    }\n</script>\n\n<style>\n    \n</style>","undoManager":{"mark":101,"positi